<?phpif(isset($hut)) {	$name  = $hut['name'] ;	$description = $hut ['description'] ;	$price = $hut['price'] ;	$state = $hut['state'] ;	$workers_assigned = $hut['workers'] ;	$action = 'save' ;	$param  = '&hut_id='.$hut['id'] ;	$message = 'EDIT HUT' ;} else {	$name        = '' ;	$description = '' ;	$price       = '' ;	$action      = 'add' ;	$param       = '' ;	$message = 'ADD NEW HUT' ;}echo '<p>'.$message.'</p>' ;?><form  id="hut" action="<?php echo SF ?>?c=huts&a=<?php echo $action.$param ?>" method="post">  <table>	<tr>		<td>Name</td>		<td><input style="width: 200px;" type="text" name="name" value="<?php echo $name ?>"/></td>	</tr>	<tr>		<td>Description</td>		<td><textarea style="width: 200px; height: 100px" name="description" ><?php echo $description ?></textarea></td>	</tr>	<tr>		<td>Price</td>		<td><input style="width: 100px;" type="text" name="price" value="<?php echo $price ?>"/></td>	</tr>	<tr>		<td>State</td>		<td>			<select name="state">			<?php			foreach($state_enum as $value) {				if (isset($state)) {					if ($value == $state)						$se = 'selected="selected"' ;					else 						$se = '' ;				} else {					$se = '' ;				}				echo '<option value="'.$value.'" '.$se.'>'.$value.'</option>';			}			?>			</select>		</td>	</tr><tr><td>Workers</td><td><select id="workers" class="required" multiple name="workers[]" title="Please select one or more workers" size="7">	<?php	foreach($workers_list as $worker) {		if (isset($workers_assigned)) {			if (in_array($worker, $workers_assigned))				$se = 'selected="selected"' ;			else 				$se = '' ;		} else {			$se = '' ;		}		echo '<option value="'.$worker['id'].'" '.$se.'>'.$worker['name'].'</option>';	}	?></select></td></tr><tr><td colspan="2"><input type="submit" name="submit"  value="<?php echo ucfirst($action) ?>" /></td></tr></table>    </form>