<?phpif (empty($huts_list)) {	echo '<p>No Huts! '.new_hut().'</p>' ;} else {	echo '<p>Huts: '.new_hut().'</p>' ;	echo '<table>' ;		echo '<tr><th>Name</th><th>Description</th><th>Price</th><th>State</th><th>Workers</th><th colspan="2">Actions</th></tr>' ;	foreach($huts_list as $hut) {		echo '<tr>' ;			echo '<td>'.$hut['name'].'</td>' ;			echo '<td>'.$hut['description'].'</td>' ;			echo '<td>'.$hut['price'].'</td>' ;			echo '<td>'.$hut['state'].'</td>' ;						echo '<td>' ;				foreach($hut['workers'] as $worker) {					echo $worker['name'].' ' ;				}			echo '</td>' ;			echo '<td><a href="'.SF.'?c=huts&a=edit-form&hut_id='.$hut['id'].'">[edit]</a></td>' ;			echo '<td><a href="'.SF.'?c=huts&a=delete-hut&hut_id='.$hut['id'].'">[delete]</a></td>' ;		echo '</tr>' ;	}	echo '</table>' ;}function new_hut() {	return '<a href="'.SF.'?c=huts&a=add-form">[Add New Hut]</a>' ;}?>